package controller;import model.Material;import java.util.ArrayList;import java.util.List;public class MaterialManager {    List<Material> materialList = new ArrayList<>();    public List<Material> getMaterialList() {        return materialList;    }    public MaterialManager(List<Material> materialList) {        this.materialList = materialList;    }    public void setMaterialList(List<Material> materialList) {        this.materialList = materialList;    }    public void addMaterial(Material material) {        materialList.add(material);    }    public void updateMaterial(Material material) {        int index = materialList.indexOf(material);        materialList.set(index, material);    }    public void deleteMaterial(Material material) {        materialList.remove(material);    }    public void display() {        for (Material material : materialList) {            System.out.println(material);        }    }}